import { useState, useEffect } from "react";

function CommentSection({ voteId }) {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [editingCommentId, setEditingCommentId] = useState(null);
  const [editContent, setEditContent] = useState("");
  const [message, setMessage] = useState("");

  const auth = JSON.parse(localStorage.getItem("auth") || "null");

  // Fetch comments
  useEffect(() => {
    const fetchComments = async () => {
      try {
        const res = await fetch(`/api/comments/vote/${voteId}`);
        const data = await res.json();
        if (res.ok) {
          setComments(data);
        } else {
          console.error("Failed to fetch comments:", data.message);
        }
      } catch (error) {
        console.error("Error fetching comments:", error);
      } finally {
        setLoading(false);
      }
    };

    if (voteId) {
      fetchComments();
    }
  }, [voteId]);

  // Submit new comment
  const handleSubmitComment = async (e) => {
    e.preventDefault();

    if (!auth?.token) {
      setMessage("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      return;
    }

    if (!newComment.trim()) {
      setMessage("ÎåìÍ∏Ä ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      return;
    }

    setSubmitting(true);
    setMessage("");

    try {
      const res = await fetch(`/api/comments/vote/${voteId}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${auth.token}`,
        },
        body: JSON.stringify({ content: newComment }),
      });

      const data = await res.json();
      if (res.ok) {
        setComments([data, ...comments]);
        setNewComment("");
        setMessage("ÎåìÍ∏ÄÏù¥ ÏûëÏÑ±ÎêòÏóàÏäµÎãàÎã§.");
      } else {
        setMessage(data.message || "ÎåìÍ∏Ä ÏûëÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
      }
    } catch (error) {
      setMessage("ÎåìÍ∏Ä ÏûëÏÑ± Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    } finally {
      setSubmitting(false);
    }
  };

  // Delete comment
  const handleDeleteComment = async (commentId) => {
    if (!window.confirm("Ï†ïÎßê Ïù¥ ÎåìÍ∏ÄÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?")) {
      return;
    }

    try {
      const res = await fetch(`/api/comments/${commentId}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${auth.token}`,
        },
      });

      if (res.ok) {
        setComments(comments.filter(comment => comment._id !== commentId));
        setMessage("ÎåìÍ∏ÄÏù¥ ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.");
      } else {
        const data = await res.json();
        setMessage(data.message || "ÎåìÍ∏Ä ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
      }
    } catch (error) {
      setMessage("ÎåìÍ∏Ä ÏÇ≠Ï†ú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    }
  };

  // Start editing comment
  const startEditComment = (comment) => {
    setEditingCommentId(comment._id);
    setEditContent(comment.content);
  };

  // Cancel editing
  const cancelEdit = () => {
    setEditingCommentId(null);
    setEditContent("");
  };

  // Submit edited comment
  const handleEditComment = async (commentId) => {
    if (!editContent.trim()) {
      setMessage("ÎåìÍ∏Ä ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      return;
    }

    try {
      const res = await fetch(`/api/comments/${commentId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${auth.token}`,
        },
        body: JSON.stringify({ content: editContent }),
      });

      const data = await res.json();
      if (res.ok) {
        setComments(comments.map(comment => 
          comment._id === commentId ? data : comment
        ));
        setEditingCommentId(null);
        setEditContent("");
        setMessage("ÎåìÍ∏ÄÏù¥ ÏàòÏ†ïÎêòÏóàÏäµÎãàÎã§.");
      } else {
        setMessage(data.message || "ÎåìÍ∏Ä ÏàòÏ†ïÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
      }
    } catch (error) {
      setMessage("ÎåìÍ∏Ä ÏàòÏ†ï Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    }
  };

  // Format date
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInMinutes = Math.floor((now - date) / (1000 * 60));

    if (diffInMinutes < 1) return "Î∞©Í∏à Ï†Ñ";
    if (diffInMinutes < 60) return `${diffInMinutes}Î∂Ñ Ï†Ñ`;
    if (diffInMinutes < 1440) return `${Math.floor(diffInMinutes / 60)}ÏãúÍ∞Ñ Ï†Ñ`;
    return date.toLocaleDateString("ko-KR");
  };

  if (loading) {
    return (
      <div style={{ textAlign: "center", padding: "20px", color: "#666" }}>
        ÎåìÍ∏ÄÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ë...
      </div>
    );
  }

  return (
    <div style={{ marginTop: "40px", borderTop: "2px solid #eee", paddingTop: "30px" }}>
      <h3 style={{ marginBottom: "20px", color: "#333" }}>
        üí¨ ÎåìÍ∏Ä ({comments.length})
      </h3>

      {/* Comment Form */}
      {auth ? (
        <form onSubmit={handleSubmitComment} style={{ marginBottom: "30px" }}>
          <div style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
            <textarea
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              placeholder="ÎåìÍ∏ÄÏùÑ ÏûëÏÑ±Ìï¥Ï£ºÏÑ∏Ïöî... (ÏµúÎåÄ 500Ïûê)"
              maxLength={500}
              rows={4}
              style={{
                width: "100%",
                padding: "12px",
                border: "2px solid #ddd",
